        public async Task CheckSingleMgcCodes(string costTitle, string contentType, string productionType, string mediaType, string ovalType, string expectedMgCode, string expectedGlCode)
        {
            // Arrange
            var latestRevision = await SetupProductionCost(costTitle, contentType, productionType, new[] { mediaType }, new[] { ovalType });

            // Act
            var b = await LedgerMaterialCodeService.GetLedgerMaterialCodes(latestRevision.Id);

            // Assert
            b.Should().NotBeNull();
            b.GlCode.Should().Be(expectedGlCode);
            b.MgCode.Should().Be(expectedMgCode);
        }

