        public async Task CheckMultipleMgcCodes(string costTitle, string contentType, string productionType, string[] mediaTypes, string[] ovalTypes, string expectedMgCode, string expectedGlCode)
        {
            // Arrange
            var latestRevision = await SetupProductionCost(costTitle, contentType, productionType, mediaTypes, ovalTypes);

            // Act
            var b = await LedgerMaterialCodeService.GetLedgerMaterialCodes(latestRevision.Id);

            // Assert
            b.Should().NotBeNull();
            b.GlCode.Should().Be(expectedGlCode);
            b.MgCode.Should().Be(expectedMgCode);
        }

        private async Task<CostStageRevision> SetupProductionCost(string costTitle, string contentType, string productionType, string[] mediaTypes, string[] ovalTypes)
        {
            var cost = await CreateProductionCost(User, costTitle, contentType, productionType);
            var latestStage = await GetCostLatestStage(cost.Id, User);
            var latestRevision = await GetCostLatestRevision(cost.Id, latestStage.Id, User);

            foreach (var mediaType in mediaTypes)
            {
                foreach (var ovalType in ovalTypes)
                {
                    var mediaTypeId = await GetMediaTypeId(mediaType);

                    Guid? ovalTypeId = null;
                    if (!string.IsNullOrEmpty(ovalType))
                    {
                        ovalTypeId = await GetOvalTypeId(ovalType);
                    }

                    await CreateMediaTypeMappingIfNotCreated(mediaTypeId);
                    await CreateExpectedAsset(cost.Id, latestStage.Id, latestRevision.Id, new CreateExpectedAssetModel
                    {
                        Title = "Title1",
                        AdId = Guid.NewGuid().ToString().Replace("-", string.Empty).Substring(0, 12),
                        AiringRegions = new[] { "EMEA" },
                        Duration = TimeSpan.FromHours(1).ToString(),
                        Definition = "HD",
                        MediaTypeId = mediaTypeId,
                        OvalTypeId = ovalTypeId,
                        FirstAirDate = DateTime.UtcNow.AddDays(5),
                        AssetId = "ADGADG",
                        Scrapped = false
                    });
                }
            }
            
            await LedgerMaterialCodeService.UpdateLedgerMaterialCodes(latestRevision.Id);
            return latestRevision;
        }
    }
